mapper.select:
SELECT


,address.yonghu_id as addressYonghuId
,address.address_name as addressName
,address.address_phone as addressPhone
,address.address_dizhi as addressDizhi
,address.isdefault_types as isdefaultTypes
,meishi.meishi_uuid_number as meishiUuidNumber
,meishi.meishi_name as meishiName
,meishi.shangjia_id as meishiShangjiaId
,meishi.meishi_photo as meishiPhoto
,meishi.meishi_types as meishiTypes
,meishi.meishi_kucun_number as meishiKucunNumber
,meishi.meishi_old_money as meishiOldMoney
,meishi.meishi_new_money as meishiNewMoney
,meishi.meishi_clicknum as meishiClicknum
,meishi.meishi_content as meishiContent
,meishi.shangxia_types as shangxiaTypes
,meishi.meishi_delete as meishiDelete
,yonghu.yonghu_name as yonghuName
,yonghu.yonghu_phone as yonghuPhone
,yonghu.yonghu_id_number as yonghuIdNumber
,yonghu.yonghu_photo as yonghuPhoto
,yonghu.yonghu_email as yonghuEmail
,yonghu.new_money as newMoney
,yonghu.status_types as statusTypes

FROM meishi_order  a
left JOIN address address ON a.address_id = address.id
left JOIN meishi meishi ON a.meishi_id = meishi.id
left JOIN yonghu yonghu ON a.yonghu_id = yonghu.id



        and a.id in

        #{item}



        and a.meishi_order_uuid_number like CONCAT('%',#{params.meishiOrderUuidNumber},'%')


        and (
            a.address_id = #{params.addressId}
        )


        and (
            a.meishi_id = #{params.meishiId}
        )


        and (
            a.yonghu_id = #{params.yonghuId}
        )


          and a.buy_number >= #{params.buyNumberStart}   


          and a.buy_number <= #{params.buyNumberEnd}   


        and a.buy_number = #{params.buyNumber}


          and UNIX_TIMESTAMP(a.meishi_order_time) >= UNIX_TIMESTAMP(#{params.meishiOrderTimeStart}) 


          and UNIX_TIMESTAMP(a.meishi_order_time) <= UNIX_TIMESTAMP(#{params.meishiOrderTimeEnd}) 


          and a.meishi_order_true_price >= #{params.meishiOrderTruePriceStart}   


          and a.meishi_order_true_price <= #{params.meishiOrderTruePriceEnd}   


        and a.meishi_order_courier_name like CONCAT('%',#{params.meishiOrderCourierName},'%')


        and a.meishi_order_courier_number like CONCAT('%',#{params.meishiOrderCourierNumber},'%')


        and a.meishi_order_types = #{params.meishiOrderTypes}


        and a.meishi_order_payment_types = #{params.meishiOrderPaymentTypes}


          and UNIX_TIMESTAMP(a.insert_time) >= UNIX_TIMESTAMP(#{params.insertTimeStart}) 


          and UNIX_TIMESTAMP(a.insert_time) <= UNIX_TIMESTAMP(#{params.insertTimeEnd}) 




        and a.address_id IS NOT NULL


        and address.address_name like CONCAT('%',#{params.addressName},'%')


        and address.address_phone like CONCAT('%',#{params.addressPhone},'%')


        and address.address_dizhi like CONCAT('%',#{params.addressDizhi},'%')


        and address.isdefault_types = #{params.isdefaultTypes}



          and UNIX_TIMESTAMP(address.insert_time) >= UNIX_TIMESTAMP(#{params.insertTimeStart}) 


          and UNIX_TIMESTAMP(address.insert_time) <= UNIX_TIMESTAMP(#{params.insertTimeEnd}) 


          and UNIX_TIMESTAMP(address.update_time) >= UNIX_TIMESTAMP(#{params.updateTimeStart}) 


          and UNIX_TIMESTAMP(address.update_time) <= UNIX_TIMESTAMP(#{params.updateTimeEnd}) 



        and a.meishi_id IS NOT NULL


        and meishi.meishi_uuid_number like CONCAT('%',#{params.meishiUuidNumber},'%')


        and meishi.meishi_name like CONCAT('%',#{params.meishiName},'%')


        and meishi.shangjia_id = #{params.shangjiaId}


        and meishi.meishi_types = #{params.meishiTypes}



          and meishi.meishi_kucun_number >= #{params.meishiKucunNumberStart}   


          and meishi.meishi_kucun_number <= #{params.meishiKucunNumberEnd}   


        and meishi.meishi_kucun_number = #{params.meishiKucunNumber}


          and meishi.meishi_old_money >= #{params.meishiOldMoneyStart}   


          and meishi.meishi_old_money <= #{params.meishiOldMoneyEnd}   


          and meishi.meishi_new_money >= #{params.meishiNewMoneyStart}   


          and meishi.meishi_new_money <= #{params.meishiNewMoneyEnd}   


          and meishi.meishi_clicknum >= #{params.meishiClicknumStart}   


          and meishi.meishi_clicknum <= #{params.meishiClicknumEnd}   


        and meishi.meishi_clicknum = #{params.meishiClicknum}


        and meishi.meishi_content like CONCAT('%',#{params.meishiContent},'%')


        and meishi.shangxia_types = #{params.shangxiaTypes}



          and meishi.meishi_delete >= #{params.meishiDeleteStart}   


          and meishi.meishi_delete <= #{params.meishiDeleteEnd}   


        and meishi.meishi_delete = #{params.meishiDelete}


          and UNIX_TIMESTAMP(meishi.insert_time) >= UNIX_TIMESTAMP(#{params.insertTimeStart}) 


          and UNIX_TIMESTAMP(meishi.insert_time) <= UNIX_TIMESTAMP(#{params.insertTimeEnd}) 



        and a.yonghu_id IS NOT NULL


        and yonghu.yonghu_name like CONCAT('%',#{params.yonghuName},'%')


        and yonghu.yonghu_phone like CONCAT('%',#{params.yonghuPhone},'%')


        and yonghu.yonghu_id_number like CONCAT('%',#{params.yonghuIdNumber},'%')


        and yonghu.yonghu_email like CONCAT('%',#{params.yonghuEmail},'%')


          and yonghu.new_money >= #{params.newMoneyStart}   


          and yonghu.new_money <= #{params.newMoneyEnd}   


        and yonghu.status_types = #{params.statusTypes}



order by a.${params.sort} ${params.order}
